// Get the raw JSON string from AI response
const rawJsonString = $input.first().json.content?.parts?.[0]?.text || '';

if (!rawJsonString.trim()) {
  return [{
    json: {
      error: "No scoring data found"
    }
  }];
}

try {
  // Parse the JSON
  const parsed = JSON.parse(rawJsonString);

  // Return flat or nested structure ‚Äî your choice!
  return [{
    json: {
      totalScore: parsed.totalScore || 0,
      skillsScore: parsed.breakdown?.skillsScore || 0,
      experienceScore: parsed.breakdown?.experienceScore || 0,
      noticePeriodScore: parsed.breakdown?.noticePeriodScore || 0,
      educationScore: parsed.breakdown?.educationScore || 0,
      companyScore: parsed.breakdown?.companyScore || 0,
      bonusScore: parsed.breakdown?.bonusScore || 0,
      reasoning: parsed.reasoning || "Not available"
    }
  }];
} catch (error) {
  return [{
    json: {
      error: "Failed to parse scoring JSON",
      rawInput: rawJsonString
    }
  }];
}

const resumeText = $input.first().json.resume || $input.first().json.text || '';

if (!resumeText || !resumeText.trim()) {
  return [{
    json: {
      summary: "Not available",
      fullName: "Not available",
      email: "Not available",
      phone: "Not available",
      location: "Not available",
      experience: "Not available",
      skills: ["Not available"],
      education: "Not available",
      currentCompany: "Not available",
      jobRole: "Not available",
      noticePeriod: "Not available"
    }
  }];
}

const lines = resumeText
  .split('\n')
  .map(line => line.trim())
  .filter(line => line !== '');

const result = {
  summary: "Not available",
  fullName: "Not available",
  email: "Not available",
  phone: "Not available",
  location: "Not available",
  experience: "Not available",
  skills: ["Not available"],
  education: "Not available",
  currentCompany: "Not available",
  jobRole: "Not available",
  noticePeriod: "Not available"
};

// --- 1. SUMMARY: first 1‚Äì2 lines until a line looks like a header or contact ---
let summaryLines = [];
for (let i = 0; i < lines.length; i++) {
  const line = lines[i];
  // Stop if we hit contact info, all-caps, or section headers
  if (
    line.includes('@') ||
    line.match(/^\+?\d/) ||
    line === line.toUpperCase() ||
    ['skills', 'work experience', 'education', 'projects'].some(hdr => line.toLowerCase().includes(hdr))
  ) {
    break;
  }
  summaryLines.push(line);
}
result.summary = summaryLines.join(' ').trim() || "Not available";

// --- 2. Name: line before "Skills" ---
const skillsIndex = lines.findIndex(line => line.toLowerCase() === 'skills');
if (skillsIndex > 0) {
  result.fullName = lines[skillsIndex - 1].replace(/[^A-Za-z\s]/g, ' ').replace(/\s+/g, ' ').trim() || "Not available";
}

// --- 3. Email ---
const emailMatch = resumeText.match(/[\w.-]+@[\w.-]+\.[a-zA-Z]{2,}/);
result.email = emailMatch ? emailMatch[0] : "Not available";

// --- 4. Phone ---
const phoneMatch = resumeText.match(/(?:\+?\d{1,3}[-.\s]?)?\(?\d{3}\)?[-.\s]?\d{3}[-.\s]?\d{4}/);
result.phone = phoneMatch ? phoneMatch[0] : "Not available";

// --- 5. Location ---
const locationLine = lines.find(line => 
  line.includes(',') && 
  (line.toLowerCase().includes('india') || line.match(/^[A-Z][a-z]+,\s*[A-Z][a-z]+/))
);
result.location = locationLine || "Not available";

// --- 6. Experience ---
const expMatch = resumeText.match(/(\d+(?:\.\d+)?)\s*(?:years?|yrs?)/i);
result.experience = expMatch ? parseFloat(expMatch[1]) : "Not available";

// --- 7. Skills ---
if (skillsIndex !== -1) {
  const workExpIndex = lines.findIndex((line, i) => i > skillsIndex && line.toLowerCase().includes('work experience'));
  const end = workExpIndex !== -1 ? workExpIndex : lines.length;
  const skillList = lines.slice(skillsIndex + 1, end).filter(line => line);
  result.skills = skillList.length > 0 ? skillList : ["Not available"];
}

// --- 8. Education ---
const eduIndex = lines.findIndex(line => line.toLowerCase() === 'education');
if (eduIndex !== -1 && eduIndex + 1 < lines.length) {
  result.education = lines[eduIndex + 1] || "Not available";
}

// --- 9. Current Company & Job Role ---
const workExpIndex = lines.findIndex(line => line.toLowerCase().includes('work experience'));
if (workExpIndex !== -1 && workExpIndex + 1 < lines.length) {
  const jobLine = lines[workExpIndex + 1];
  if (jobLine.includes('‚Äî') || jobLine.includes('-')) {
    const [role, company] = jobLine.split(/‚Äî|-/).map(s => s.trim());
    result.jobRole = role || "Not available";
    result.currentCompany = company || "Not available";
  } else {
    result.jobRole = jobLine || "Not available";
    result.currentCompany = "Not available";
  }
}

return [{ json: result }];
